    start = 0
    end = 0
    for i in range(lcs_size+1):
        for j in range(lcs_size+1):
            if i == j:
                lcs[i][j] = 1
            elif i > j:
                if j == lcs_size:
                    max_length = lcs[i][j]
                    start = i
                    end = j
            lcs[i][j] = max_length

