private void GenerateChunk(int index)
{
    Vector3 position = new Vector3(index * chunkSize, 0, 0);
    TerrainData terrainData = GenerateProceduralTerrain(position);
    terrainDatas.Add(terrainData);

    GameObject terrainGameObject = GameObject.Instantiate(VistaSettings.instance.terrainPrefab, position, Quaternion.identity);
    terrainGameObject.transform.localScale = new Vector3(chunkSize, 1, chunkSize);
    terrainGameObject.GetComponent<Terrain>().terrainData = terrainData;
    terrainGameObjects.Add(terrainGameObject);
}

private TerrainData GenerateProceduralTerrain(Vector3 position)
{
    HeightmapGenerator heightmapGenerator = new HeightmapGenerator();
    HeightmapData heightmapData = heightmapGenerator.GenerateNoiseMap(new IntVector2((int)(position.x / chunkSize), (int)(position.z / chunkSize)), chunkSize, chunkSize, 10, 10);
    TerrainData terrainData = new TerrainData();
    terrainData.heightmap = heightmapData.heightmap;
    terrainData.size = new Vector3(chunkSize, 1, chunkSize);
    terrainData.SetAlphamaps(0, VistaSettings.instance.grassAlphaMap);
    terrainData.SetAlphamaps(1, VistaSettings.instance.treeAlphaMap);
    terrainData.SetAlphamaps(2, VistaSettings.instance.waterAlphaMap);
    terrainData.SetColors(0, VistaSettings.instance.grassColor);
    terrainData.SetColors(1, VistaSettings.instance.treeColor);
    terrainData.SetColors(2, VistaSettings.instance.waterColor);
    terrainData.splatmap = new Texture2D(chunkSize, chunkSize);
