{
    float playerDistanceSquared = Mathf.Pow(playerDistance, 2);

    for (int i = 0; i < terrainGameObjects.Count; i++)
    {
        float distanceSquared = Vector3.Distance(transform.position, terrainGameObjects[i].transform.position).sqrMagnitude;

        if (distanceSquared > playerDistanceSquared)
        {
            UnloadTerrain(i);
        }
        else if (!IsTerrainVisible(i))
        {
            GenerateMissingTerrain(i);
        }
    }
}

private bool IsTerrainVisible(int index)
{
    Vector3 terrainPosition = terrainGameObjects[index].transform.position;
    Vector3 playerPosition = transform.position;
    Vector3 direction = terrainPosition - playerPosition;

    float angle = Vector3.Angle(direction, Vector3.forward);

    if (angle < playerDistance)
    {
        return true;
    }

    return false;
}

private void GenerateMissingTerrain(int index)
{
    StartCoroutine(GenerateChunk(index));
}

private void UnloadTerrain(int index)
{
    GameObject terrainGameObject = terrainGameObjects[index];
    TerrainData terrainData = terrainDatas[index];

    terrainDatas.RemoveAt(index);
    terrainGameObjects.RemoveAt(index);

    GameObject.Destroy(terrainGameObject);
}
