    "time"
)

type Budget struct {
    Weights []float64
    Fitness  float64
}

func (b *Budget) calculateFitness() {
    // Here you would implement your logic to calculate the fitness
    // of a particular budget allocation. This will depend on your
    // specific use case.
    b.Fitness = 0
    for _, weight := range b.Weights {
        b.Fitness += weight * weight // Dummy fitness function
    }
}

func mutate(b *Budget, mutationRate float64) {
    for i := range b.Weights {
        if rand.Float64() < mutationRate {
            b.Weights[i] += rand.Float64()*0.2 - 0.1 // Dummy mutation
        }
    }
}

func crossover(parent1, parent2 *Budget) *Budget {
    child := &Budget{
        Weights: make([]float64, len(parent1.Weights)), len(parent1.Weights)),
    }
    crossoverPoint := rand.Intn(len(parent1.Weights)) - 1)
    for i := range child.Weights {
        if i <= crossoverPoint {
            child.Weights[i] = parent1.Weights[i]
        } else {
            child.Weights[i] = parent2.Weights[i]
        }
    }
    return child
}

func main() {
    rand.Seed(time.Now().UnixNano())
    populationSize := 100
    budgets := make([]*Budget, populationSize)
    for i := range budgets {
        budgets[i] = &Budget{
            Weights: []float64{rand.Float64(), rand.Float64(), rand.Float64()}, // Dummy weights
        }
        budgets[i].calculateFitness()
    }
    for generation := 0; generation < 100; generation++ {
        newBudgets := make([]*Budget, populationSize)
        for i := range newBudgets {
            parent1 := selectParent(budgets)
            parent2 := selectParent(budgets)
            child := crossover(parent1, parent2)
            mutate(child, 0.01)
            child.calculateFitness()
            newBudgets[i] = child
        }
        budgets = newBudgets
        fmt.Printf("Generation %d: Best fitness = %f\n", generation, getBestFitness(budgets))
    }
}

func selectParent(budgets []*Budget) *Budget {
    // Tournament selection
    parent := budgets[rand.Intn(len(budgets)))]
    for i := 0; i < 3; i++ {
        opponent := budgets[rand.Intn(len(budgets)))]
        if opponent.Fitness > parent.Fitness {
            parent = opponent
        }
    }
    return parent
}

func getBestFitness(budgets []*Budget) float64 {
    bestFitness := -1.0
    for _, budget := range budgets {
        if budget.Fitness > bestFitness {
            bestFitness = budget.Fitness
        }
    }
    return bestFitness
}
