      // Copy the file
      if (copy(file_name, destination_dir) == -1) {
        perror("copy");
        nftw_destroy(nftw);
        return;
      }
    }
  } else {
    // Move the files
    nftw_walk(nftw, source_dir, NULL, NULL, NULL, NULL);
    for (int i = 0; i < nftw_num_files(nftw); i++) {
      char *file_name = nftw_file_name(nftw, i);
      char *file_extension = strrchr(file_name, '.');
      if (file_extension != NULL && strcmp(file_extension, ".") == 0) {
        // Skip the current file
        continue;
      }
      if (strcmp(file_extension, extension_list[0]) == 0) {
        // Skip the file if it is in the extension list
        continue;
      }
      // Move the file
      if (rename(file_name, destination_dir) == -1) {
        perror("rename");
        nftw_destroy(nftw);
        return;
      }
    }
  }

  // Destroy the nftw structure
  nftw_destroy(nftw);
}

int main(int argc, char *argv[]) {
  if (argc < 3) {
    fprintf(stderr, "Usage: %s <source_dir> <destination_dir> [option] <extension_list>\n", argv[0]);
    return 1;
  }

  // Check if the source directory exists
