input int TrendFollowingPeriod = 20;
input int StopLoss = 50;
input int TakeProfit = 100;
input int InitialDeposit = 10000;
input int InitialCapital = 10000;
input int LotSize = 0.01;
input int MaxDrawdown = 10;
input int MaxTrades = 10;

// Global variables
global double Equity;
global int TradesCount;

// Initialize
if (StrStr(SymbolInfo("SymbolDescription"), "Future") < 0)
{
  Print("This EA is designed for Forex Futures only.");
  Delete();
  return;
}

Equity = InitialCapital;
TradesCount = 0;

