int MA1, MA2;

if (Close[1] > Close[0])
{
  MA1 = MovingAverage(Close, MovingAveragePeriod1);
  MA2 = MovingAverage(Close, MovingAveragePeriod2);

  if (MA1 > MA2)
  {
    if (OrderSend(Symbol(), OP_BUY, LotSize, Ask, Slippoint=0, 0) == ORDER_FILLED)
    {
      Print("Buy at", Ask);
      TradesCount++;
    }
  }
}

// Trend Following
int Trend;
int TrendDirection;

if (Close[1] > Close[0])
{
  Trend = TrendUp(Close, TrendFollowingPeriod);
  TrendDirection = 1;
}
else if (Close[1] < Close[0])
{
  Trend = TrendDown(Close, TrendFollowingPeriod);
  TrendDirection = -1;
}
else
{
  Trend = 0;
}

if (Trend != 0 && TradesCount < MaxTrades && Equity > InitialDeposit + MaxDrawdown)
{
  double EntryPrice = Ask;
  double StopLossLevel = Ask - StopLoss * Point;
  double TakeProfitLevel = Ask + TakeProfit * Point;

  if (OrderSend(Symbol(), OP_BUY, LotSize, Ask, Slippoint=0, StopLoss=StopLossLevel, TakeProfit=TakeProfitLevel) == ORDER_FILLED)
  {
    Print("Buy at", Ask);
    TradesCount++;
  }
}

// Risk Management
if (TradesCount > 0)
{
  double OpenProfit = (Ask - EntryPrice) * LotSize;
  double Drawdown = Equity - (EntryPrice * LotSize * (1 + OpenProfit / LotSize));

  if (Drawdown > MaxDrawdown)
  {
