    for (int i = OrdersTotal() - 1; i >= 0; i--)
    {
      if (OrdersOpen(i).Symbol == Symbol() && OrdersOpen(i).MagicNumber == 0)
      {
        OrderClose(OrdersOpen(i).OrderID, ClosePrice);
      }
    }
  }
}
