        // Generate a list of all the terrain chunks
        for (int x = 0; x < terrainSize; x += terrainResolution)
        {
            for (int z = 0; z < terrainSize; z += terrainResolution)
            {
                // Create a new terrain chunk
                TerrainChunk chunk = new TerrainChunk(x, z, terrainResolution);

                // Add the chunk to the list
                chunks.Add(chunk);
            }
        }
    }

    void Update()
