```python
import os
from PIL import Image
import hashlib

def get_image_hash(image_path):
    # Open the image file
    with open(image_path, 'rb') as f:
        # Read the image data
        data = f.read()
        # Create a hash of the image data
        return hashlib.md5(data).hexdigest()

def delete_duplicate_images(folder):
    # Dictionary to store hashes of images
    hashes = {}
    for root, dirs, files in os.walk(folder):
        for file in files:
            if file.lower().endswith(('.png', '.jpg', '.jpeg', '.gif', '.bmp')):
                file_path = os.path.join(root, file)
                try:
                    # Get the hash of the image
                    image_hash = get_image_hash(file_path)
                    # Check if the hash is already in the dictionary
                    if image_hash in hashes:
                        # If the file path is different, delete the older file
                        if hashes[image_hash] != file_path:
                            os.remove(hashes[image_hash])
                            print(f"Deleted duplicate: {hashes[image_hash]}")
                    else:
                        # If the hash is not in the dictionary, add it
                        hashes[image_hash] = file_path
                except Exception as e:
                    print(f"Error processing {file_path}: {e}")

# Replace 'your_directory_path' with the path of the directory you want to process
