% Define the function f(x)
function y = f(x)
    y = 10*(x(2) - x(1)^2).^2 + (1 - x(1)).^2;
end

% Define the gradient of f(x)
function grad_f = grad_f(x)
    grad_f = [-2*x(1)*(10*x(2) - 2*x(1)^2) - 2*(1 - x(1)); 20*(x(2) - x(1)^2)];
end

% Define the Hessian matrix B_k of f(x)
function B_k = B_k(x)
    B_k = [2*10, -4*x(1); -4*x(1), 20];
end

% Define the quadratic model m_k(p)
function y = m_k(p, x)
    y = f(x) + grad_f(x)' * p + 0.5 * p' * B_k(x) * p;
end

% Define the trust region radius
Delta = linspace(0, 2, 5);

% Define the points x
x1 = linspace(-1, 1, 100);
x2 = linspace(-1, 1, 100);
[X, Y] = meshgrid(x1, x2);

% Calculate the values of m_k(p) at the grid points
Z = zeros(size(X));
for i = 1:size(X, 1)
    for j = 1:size(X, 2)
        Z(i, j) = m_k([X(i, j), Y(i, j)], [0, -1]);
    end
end

% Plot the contour lines of m_k(p) at different trust region radii
figure;
for i = 1:length(Delta)
    contour(X, Y, Z, Delta(i), 'LineWidth', 2);
